// The first line contains an integer n, the number of steps in Gary's hike.
// The second line contains a single string s, of n characters that describe his path.
// Турист ходит по горам и долинам: U - вверх, D - вниз. Стартует с уровня моря, посчитать количество долин, в которые он опустится,
// т.е. между двумя нулевыми точками (уровень моря) и есть долина. Если в долине две мелких "долинки", то всё равно это одна большая долина, а не две
const n = 8,
    s = 'UDDDUDUU', // 1
    s2 = 'DDUUDDUDUUUD', // 2
    s3 = 'DDUDUDDUDDUDDUUUUDUDDDUUDDUUDDDUUDDUUUUUUDUDDDDUDDUUDUUDUDUUUDUUUUUDDUDDDDUDDUDDDDUUUUDUUDUUDUUDUDDD', // 5
    s4 = ''; // 18


function countingValleys(n, s) {
    let array = s.split(''),
        level = 0,
        previous = 0,
        current = 0,
        counter = 0;
    for (const item of array) {
        previous = level;
        level = item === 'U' ? ++level : --level;
        current = level;
        counter = previous === 0 && current === -1 ? ++counter : counter;
    }
    return counter;
}

console.log(countingValleys(n, s3));
